import React, { Component } from 'react';
import { Service } from '../db.js';


class AddTeam extends Component {

  constructor() {
    super();
    this.state = {
      name: '',
      dt: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTeam = this.addTeam.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTeam(event) {
    event.preventDefault();
    {({ add }) => {
      add({ name: this.state.name, dt: this.state.dt }).then(
        event => {
          console.log('ID Generated: ', event.target.result);
        },
        error => {
          console.log(error);
        }
      );
    }}
  }

  render (){
    return (
      <div className="AddTeam">
        <div className="card">
          <div className="card-content">
            <form onSubmit={this.addTeam}>
              <h5>Agregar Equipo</h5>
              <input name="name" onChange={this.handleChange} value={this.state.name} type="text" placeholder="Nombre del equipo"/>
              <input name="dt" onChange={this.handleChange} value={this.state.dt} type="text" placeholder="Director tecnico"/>
              <AccessDB objectStore="teams">
                <button onChange={this.addTeam} type="submit" className="btn #e64a19 deep-orange darken-2">Agregar</button>;
              </AccessDB>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default AddTeam;
