import React, { Component } from 'react';

import Select from 'react-select'

import * as DB from '../db'

class AddMatch extends Component {

  constructor() {
    super();
    this.state = {
      team1: '',
      team2: '',
      goals1: '',
      goals2: '',
      teams: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTeam = this.addTask.bind(this);
    this.updateTeams = this.updateTeams.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTask(e) {
    e.preventDefault();
  }

  updateTeams(e){
    console.log("asdsad");
    alert("NICOLLE NO TE QUIERE IDIOTA");
    this.teams = DB.fetchTeams();
  }

  render (){
    const options = this.teams;
    return (
      <div className="AddMatch">
        <div className="card">
          <div className="card-content">
            <form onSubmit={this.addTask}>
              <h5>Agregar partido</h5>
              <h6>Equipo local</h6>
              <Select options={options} onChange={this.updateTeams}/>
              <h6>Equipo visitante</h6>
              <Select options={options} onChange={this.updateTeams}/>
              <br/>
              <button type="submit" className="btn #e64a19 deep-orange darken-2">Agregar</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default AddMatch;
