import React, { Component } from 'react';

//import db from '../db';

class AddTeam extends Component {

  constructor() {
    super();
    this.state = {
      name: '',
      dt: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTeam = this.addTeam.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTeam(e) {
    e.preventDefault();

    var request = window.indexedDB.open("teams", 1);
    var db;

    request.onerror = function(event) {
      console.error('error:',event.target.errorCode);
    };

    request.onsuccess = function(event) {
      db = event.target.result;
    };

    var transaction = db.transaction(["teams"], "readwrite");
    var objectStore = transaction.objectStore("teams") ;
    var request = objectStore.add(this.state);
  }

  render (){
    return (
      <div className="AddTeam">
        <div className="card">
          <div className="card-content">
            <form onSubmit={this.addTeam}>
              <h5>Agregar Equipo</h5>
              <input name="name" onChange={this.handleChange} value={this.state.name} type="text" placeholder="Nombre del equipo"/>
              <input name="dt" onChange={this.handleChange} value={this.state.dt} type="text" placeholder="Director tecnico"/>
              <button type="submit" className="btn #e64a19 deep-orange darken-2">Agregar</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default AddTeam;
