        if(team.getName() == match.team2)
          team.calculate(match)
      });
    });

    let optionItems;
    if(teams != null){
      optionItems = teams.map((team) =>
              <option key={team.name}>{team.name}</option>
      );
    }


    return (
      <div className="Table">
        <h5>Tabla de Posiciones</h5>
        <table>
          <thead>
            <tr>
              <th>Equipo</th>
              <th>PJ</th>
              <th>G</th>
              <th>E</th>
              <th>P</th>
              <th>GF</th>
              <th>GC</th>
              <th>DG</th>
              <th>Pts</th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.teamsData.map(team => {
                return (
                  <tr>
                    <td>{team.name}</td>
                    <td>{team.pj}</td>
                    <td>{team.g}</td>
                    <td>{team.e}</td>
                    <td>{team.p}</td>
                    <td>{team.gf}</td>
                    <td>{team.gc}</td>
                    <td>{team.dg}</td>
                    <td>{team.pts}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
    );
  }
}

export default Table;
