import React, { Component } from 'react';

class AddTeam extends Component {

  constructor(props) {
    super();
    this.teamCount = 0;
    this.state = {
      name: '',
      dt: '',
      teams: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTeam = this.addTeam.bind(this);
  }

  componentDidMount(){  
    this.setState({teams: this.props.teams});
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTeam(e) {
    e.preventDefault();
    let teams = localStorage.getItem('teams');
    let arr = [];
    if(teams){
        arr = JSON.parse(teams);
        arr.push({name:this.state.name,dt:this.state.dt});
        arr = Array.from(new Set(arr));
        localStorage.setItem('teams', JSON.stringify(arr));
    }else{
        arr=[];
        arr.push({name:this.state.name,dt:this.state.dt});
        localStorage.setItem('teams', JSON.stringify(arr));
    }
    this.props.callback();
  }

  render (){
    return (
      <div className="AddTeam">
        <div className="card">
          <div className="card-content">
            <form onSubmit={this.addTeam}>
              <h5>Agregar Equipo</h5>
              <input name="name" onChange={this.handleChange} value={this.state.label} type="text" placeholder="Nombre del equipo"/>
              <input name="dt" onChange={this.handleChange} value={this.state.dt} type="text" placeholder="Director tecnico"/>

              <button onChange={this.addTeam} type="submit" className="btn #e64a19 deep-orange darken-2">Agregar</button>

            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default AddTeam;
