class DB {

  conect(){
    const dbconnect = (window.indexedDB
                     || window.webkitIndexedDB
                     || window.mozIndexedDB).open('WalnicDB', 1);
    dbconnect.onupgradeneeded = ev => {      
      const db = ev.target.result;
      var store = db.createObjectStore('Team', { keyPath: 'id', autoIncrement: true });
      store.createIndex('name', 'name', { unique: false });
      store.createIndex('dt', 'dt', { unique: true });

      store = db.createObjectStore('Match', { keyPath: 'id', autoIncrement: true });
      store.createIndex('team1', 'team1', { unique: false });
      store.createIndex('team2', 'team2', { unique: false });
      store.createIndex('goals1', 'goals1', { unique: false });
      store.createIndex('goals2', 'goals2', { unique: false });
    }
    return dbconnect;
  }

  addTeamDB(data){
    this.conect.onsuccess = ev => {
  	   const db = ev.target.result;
       const transaction = db.transaction('Team', 'readwrite');
  	   const store = transaction.objectStore('Team');
  	   store.add(data);
    }
  }

}

export default DB;
