import React, { Component } from 'react';

import * as DB from '../db'

class AddTeam extends Component {

  constructor() {
    super();
    this.state = {
      label: '',
      dt: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTeam = this.addTeam.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTeam(e) {
    e.preventDefault();
    localStorage.setItem('1','Nico');
  }

  render (){
    return (
      <div className="AddTeam">
        <div className="card">
          <div className="card-content">
            <form onSubmit={this.addTeam}>
              <h5>Agregar Equipo</h5>
              <input name="label" onChange={this.handleChange} value={this.state.label} type="text" placeholder="Nombre del equipo"/>
              <input name="dt" onChange={this.handleChange} value={this.state.dt} type="text" placeholder="Director tecnico"/>

              <button onChange={this.addTeam} type="submit" className="btn #e64a19 deep-orange darken-2">Agregar</button>

            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default AddTeam;
