import React, { Component } from 'react';


class Table extends Component {

  constructor() {
    super();
    this.state = {
      id: '',
      name: '',
      dt: '',
      teams: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTeam = this.addTask.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTask(e) {
    e.preventDefault();
  }

  render (){
    return (
      <div className="col s7">
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.teams.map(team => {
                return (
                  <tr key={team._id}>
                    <td>{team.name}</td>
                    <td>{team.description}</td>                    
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
    );
  }
}

export default Table;
