export function addTeamDB(data) {
  var DBOpenRequest = window.indexedDB.open("WalnicDB", 1);
  DBOpenRequest.onsuccess = function(event) {
    var db = DBOpenRequest.result;
    var transaction = db.transaction(["Team"], "readwrite");
    var objectStore = transaction.objectStore("Team");
    var objectStoreRequest = objectStore.add(data);
  };


};

export async function fetchTeams() {

  var DBOpenRequest = window.indexedDB.open("WalnicDB", 1);
  var teams = [];
  DBOpenRequest.onsuccess = await async function(event) {
    var db = DBOpenRequest.result;
    var tx = db.transaction('Team', 'readonly');
    var store = tx.objectStore('Team');

    // Desgraciadamente, todo sigue siendo asíncrono y lo que podría ser
    // un sencillo y lineal bucle se convierte en callbacks

    store.openCursor().onsuccess = await function(event) {
      var cursor = event.target.result;
      // cursor será truthy mientras haya elementos que procesar
      if (cursor) {
        // En cursor.value tenemos el elemento actual

        teams.push(cursor.value);
        // Pasamos a procesar el siguiente resultado
        cursor.continue();
      }
    };
  };
