var DBOpenRequest = window.indexedDB.open("WalnicDB", 1);
var db;

DBOpenRequest.onsuccess = function(event) {
  db = DBOpenRequest.result;
};

export function addTeamDB(data) {
  var transaction = db.transaction(["Team"], "readwrite");
  var objectStore = transaction.objectStore("Team");
  var objectStoreRequest = objectStore.add(data);
};

var db;
export function fetchTeams() {
  var active = db.result;
  var data = active.transaction(["Team"], "readonly");
  var object = data.objectStore("Team");
  var elements = [];
  object.openCursor().onsuccess = function (e) {
      var result = e.target.result;
      if (result === null) {
          return;
      }
      elements.push(result.value);
      result.continue();
  };
  data.oncomplete = function () {
      return elements;
  };
};
