import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import AddTeam from './AddTeam';

class AddMatch extends Component {

  constructor() {
    super();

    this.state = {
      team1: '',
      team2: '',
      goals1: '',
      goals2: '',
      teams: [],
      matchs: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.updateTeams = this.updateTeams.bind(this);

  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  updateTeams(){
    let teams = localStorage.getItem('teams');
    let arr = [];
    arr = JSON.parse(teams);
    arr = Array.from(new Set(arr));
    this.setState({teams: arr});
  }

  updateMatchs(){
    let matchs = localStorage.getItem('matchs');
    let arr = [];
    arr = JSON.parse(matchs);
    arr = Array.from(new Set(arr));
    this.setState({matchs: arr});
  }

  componentDidMount(){
    this.updateTeams();
    this.updateMatchs();
    otherObject.addEventListener('click', () => {
      this.updateTeams();
    });
  }

  addMatch(e) {
    let teams = localStorage.getItem('matchs');
    let arr = [];
    if(teams){
        arr = JSON.parse(teams);
        arr.push({team1:this.state.team1, team2:this.state.team2, goals1:this.state.goals1, goals2:this.state.goals2});
        arr = Array.from(new Set(arr));
        localStorage.setItem('matchs', JSON.stringify(arr));
    }else{
        arr=[];
        arr.push({name:this.state.name,dt:this.state.dt});
        localStorage.setItem('matchs', JSON.stringify(arr));
    }
  }

  render (){

    let teams = this.state.teams;
    let optionItems;
    if(teams != null){
      optionItems = teams.map((team) =>
              <option key={team.name}>{team.name}</option>
      );
    }

    return (
      <div className="AddMatch">
        <div className="card">
          <div className="card-content">
            <form onSubmit={this.addTask}>
              <h5>Agregar partido</h5>
              <h6>Equipo local</h6>
              <div className="input-field col s12">
                <select className="browser-default">
                  <option value="" disabled selected>Choose your option</option>
                  {optionItems}
                </select>
              </div>
              <h6>Equipo visitante</h6>
              <div className="input-field col s12">
                <select className="browser-default">
                  <option value="" disabled selected>Choose your option</option>
                  {optionItems}
                </select>
              </div>
              <br/>
              <button type="submit" className="btn #e64a19 deep-orange darken-2">Agregar</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default AddMatch;
