import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Select from 'react-select'
import Item from 'react'
import CreatableSelect from 'react-select/creatable';
import * as DB from '../db'



import $ from 'jquery';

class AddMatch extends Component {

  constructor() {
    super();
    this.state = {
      team1: '',
      team2: '',
      goals1: '',
      goals2: '',
      teams: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTeam = this.addTask.bind(this);
    this.updateTeams = this.updateTeams.bind(this);
    this.teams = DB.fetchTeams();
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTask(e) {
    e.preventDefault();
  }

  updateTeams(e){
    this.teams = DB.fetchTeams();
  }

  /*
  <h6>Equipo local</h6>
  <Select options={this.teams} onChange={this.updateTeams}/>
  <h6>Equipo visitante</h6>
  <Select options={this.teams} onChange={this.updateTeams}/>*/
  render (){

    //const options = this.teams;
    this.teams = DB.fetchTeams();


    const options = [
      { value: 'chocolate', label: 'Chocolate' },
      { value: 'strawberry', label: 'Strawberry' },
      { value: 'vanilla', label: 'Vanilla' }
    ];
    //console.log(this.teams);
    //console.log(options);
    let list = [];
    console.log(this.teams);
    for (var i = 0; i < this.teams.length; i++) {
      list.push(this.teams[i]);
    }

    //let list = options;
    let optionItems = list.map((item) =>
        <option key={item.label}>{item.label}</option>
    );


    return (
      <div className="AddMatch">
        <div className="card">
          <div className="card-content">
            <form onSubmit={this.addTask}>
              <h5>Agregar partido</h5>
              <h6>Equipo local</h6>
              <div className="input-field col s12">
                <select className="browser-default">
                  <option value="" disabled selected>Choose your option</option>
                  {optionItems}
                </select>
              </div>
              <h6>Equipo visitante</h6>
              <div className="input-field col s12">
                <select className="browser-default">
                  <option value="" disabled selected>Choose your option</option>
                  {optionItems}
                </select>
              </div>
              <br/>
              <button type="submit" className="btn #e64a19 deep-orange darken-2">Agregar</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default AddMatch;
