import React, { Component } from 'react';

import db from '../db';

class Table extends Component {

  constructor() {
    super();
    this.state = {
      teams: []
    };
  }

  fetchTeams() {
    var active =  db.result;
    var data = active.transaction(["teams"], "readonly");
    var object = data.objectStore("teams");

    this.state.teams = [];

    object.openCursor().onsuccess = function (e) {
      var result = e.target.result;
      if (result === null) {
          return;
      }
      this.state.teams.push(result.value);
      result.continue();
    };
  };

  render (){
    return (
      <div className="Table">
        <h5>Tabla de Posiciones</h5>
        <table>
          <thead>
            <tr>
              <th>Equipo</th>
              <th>PJ</th>
              <th>G</th>
              <th>E</th>
              <th>P</th>
              <th>GF</th>
              <th>GC</th>
              <th>DG</th>
              <th>Pts</th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.teams.map(team => {
                return (
                  <tr>
                    <td>{team.name}</td>
                    <td>{team.pj}</td>
                    <td>{team.g}</td>
                    <td>{team.e}</td>
                    <td>{team.p}</td>
                    <td>{team.gf}</td>
                    <td>{team.gc}</td>
                    <td>{team.dg}</td>
                    <td>{team.pts}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
    );
  }
}

export default Table;
